import pandas as pdimport numpy as np# Import Relevent Dataprediction_data = pd.read_csv('../data/TESTING_DATA_PREDICTIONS.csv')training_data = pd.read_csv('../data/TRAINING_DATA.txt')testing_data = pd.read_csv('../data/TESTING_DATA.txt')# Get Relevent Lengths for Loops Belowtesting_data_len = len(testing_data)prediction_data_len = len(prediction_data)# Make SO2 Column Vector in Testing Dataso2_predictions =  np.zeros((testing_data_len, 1))testing_data.insert(0, 'so2', so2_predictions)for i in range(0, testing_data_len):        for j in range(0, prediction_data_len):                if testing_data['car.count'][i] == prediction_data['car.count'][j]:                        testing_data['so2'][i] = prediction_data['so2'][j]        testing_data.to_csv('../prediction/TESTING_DATA_PREDICTIONS.txt', index=False) 