from datetime import datetimedef txt_to_csv(file):        file = str(file)        if '.txt' in file:                file = file.replace('.txt', '')        with open(file + '.txt') as fin, open(str(file)+'.csv', 'w') as fout:        for line in fin:            fout.write(line.replace('\t', ','))            def add_time_column(data_frame):        day_index = data_frame['day.index']    time_index = data_frame['time.of.day']/24        time_column = day_index + time_index        return data_frame.insert(0, 'time.index', time_column)def time(which_time):        if which_time == 'start':        time = datetime.now()        print('\nStart Time: {}'.format(time))        print('\n')        return time            if which_time == 'end':        time = datetime.now()        print('\nEnd Time: {}\n'.format(time))        return time            else:        print('Run Time: {}'.format(which_time))def print_shape(data_target, train_data, test_data, X_train, y_train, X_test, y_test):    print('\n\ndata_target.shape: ', data_target.shape)            print('\ntrain_data.shape: ', train_data.shape)    print('\ntest_data.shape: ', test_data.shape)        print('\nX_train.shape: ', X_train.shape)    print('\ny_train.shape: ', y_train.shape)        print('\nX_test.shape: ', X_test.shape)    print('\ny_test.shape: ', y_test.shape)    print('\n')